import java.io.Console;import java.lang.ref.Cleaner;import java.util.*;import java.io.IOException;import java.text.DecimalFormat;public class Main {static void CurrencyConverter() {    double usd, gbp, bgn, euro;    DecimalFormat f = new DecimalFormat("##.###");    Scanner sc = new Scanner(System.in);    String code;    System.out.println("Въведете курса на вулатата usd\ngbp\nbgn\nеuro");    code = sc.nextLine();    switch (code.toLowerCase()) {        case "usd": {            System.out.println("Въведете сумата в долари:");            usd = sc.nextDouble();            System.out.println("Сумата в левове:" + f.format(usd*1.76) + "\n" + "Сумата в британски лири:" + f.format(usd*0.77) + "\n" + "Сумата в евро:" + f.format(usd*0.90));            break;        }        case "bgn": {            System.out.println("Въведете сумата в левове:");            bgn = sc.nextDouble();            System.out.println("Сумата в долари:" + f.format(bgn * 0.57) + "\n" + "Сумата в британски лири:" + f.format(bgn * 0.44) + "\n" + "Сумата в евро:" + f.format(bgn * 0.51));            break;        }        case "gbp":{            System.out.println("Въведете сумата в бринтаски лири:");            gbp = sc.nextDouble();            System.out.println("Сумата в левове:"+f.format(gbp*2.29)+"\n"+"Сумата в долари:"+f.format(gbp*1.30)+"\n"+"Сумата в левове:"+f.format(gbp*1.17));            break;        }        case "euro":        {            System.out.println("Въведете сумата в евро:");            euro = sc.nextDouble();            System.out.println("Сумата в левове"+f.format(euro*1.96)+"\n"+"Сумата в долари:"+f.format(euro*1.11)+"\n"+"Сумата в британски лири:"+f.format(euro*0.85));            break;        }        default: CurrencyConverter();    }}    public static void main(String[] args)    {        CurrencyConverter();    }}